mapper.select:
SELECT CALENDAR_NUM, MEMBER_NUM, TITLE, CONTENT, START_DAY, END_DAY, ADDRESS, CALENDAR_REGDATE
FROM CALENDAR
WHERE MEMBER_NUM = #{member_num}

SELECT CALENDAR_NUM, MEMBER_NUM, TITLE, CONTENT, START_DAY, END_DAY, ADDRESS, CALENDAR_REGDATE
FROM CALENDAR
WHERE CALENDAR_NUM = #{calendar_num}

mapper.update:
UPDATE CALENDAR
SET TITLE = #{title}, CONTENT = #{content}, START_DAY = #{start_day}, END_DAY = #{end_day}, ADDRESS = #{address}
WHERE CALENDAR_NUM = #{calendar_num}
AND MEMBER_NUM = #{member_num}

mapper.delete:
DELETE FROM CALENDAR
WHERE CALENDAR_NUM = #{calendar_num}
AND MEMBER_NUM = #{member_num}
