mapper.select:
SELECT CHANNEL_NUM, CHANNEL_NAME, CHANNEL_INFORMATION, CHANNEL_ENABLED, CHANNEL_REGDATE
FROM CHANNELROOM
WHERE CHANNEL_NUM = #{channel_num}

mapper.update:
UPDATE CHANNELROOM
SET CHANNEL_NAME = #{channel_name}, CHANNEL_INFORMATION = #{channel_information}
WHERE CHANNEL_NUM = #{channel_num}

mapper.select:
SELECT COUNT(*)
FROM CHANNELROOM
WHERE MEMBER_ID = #{member_id}

mapper.update:
UPDATE MEBER
SET MEMBER_PW =
#{member_pw}
WHERE MEMBER_NUM = #{member_num}

UPDATE MEBER
SET
MEMBER_STATEMENT = #{member_statement}
WHERE MEMBER_NUM = #{member_num}
